DROP TABLE IF EXISTS `news_article`;
CREATE TABLE `news_article`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `aid` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '新闻标识',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '新闻标题',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '新闻内容',
  `channelId` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '所属频道',
  `pubDate` datetime(0) NULL DEFAULT NULL COMMENT '发布时间',
  `source` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '来源',
  `imageurls` varchar(2000) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '图片',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `aid`(`aid`) USING BTREE,
  INDEX `navigation_article`(`channelId`) USING BTREE,
  CONSTRAINT `navigation_article` FOREIGN KEY (`channelId`) REFERENCES `news_navigation` (`channelId`) ON DELETE RESTRICT ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '新闻内容' ROW_FORMAT = Dynamic;

