  `cid` int(11) NOT NULL COMMENT '评论标识',
  `uid` int(11) NOT NULL COMMENT '用户标识',
  `replyid` int(11) UNSIGNED ZEROFILL NULL DEFAULT NULL COMMENT '该评论回复的id,没有为0',
  `pid` int(11) NULL DEFAULT NULL COMMENT '该评论所在的父级评论id,没有则为0',
  `aid` varchar(11) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '所属文章标识',
  `content` varchar(11) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '评论内容',
  `createtime` datetime(0) NOT NULL COMMENT '评论时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `login_comment`(`uid`) USING BTREE,
  INDEX `article_comment`(`aid`) USING BTREE,
  UNIQUE INDEX `cid`(`cid`) USING BTREE,
  INDEX `commentself`(`pid`) USING BTREE,
  CONSTRAINT `article_comment` FOREIGN KEY (`aid`) REFERENCES `news_article` (`aid`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `commentself` FOREIGN KEY (`pid`) REFERENCES `news_comment` (`cid`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `login_comment` FOREIGN KEY (`uid`) REFERENCES `user_login` (`u_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '新闻评论' ROW_FORMAT = Dynamic;

